	testString: String;
	# Field Description for Int
	testInt: Int;
	# Field Description for ID
	testID: ID;
	# Field Description for Boolean
	testBoolean: Boolean;
	# Field Description for Float
	testFloat: Float;
}

interface ISearchable {
    searchPreview: String!;
}

union ProductTypes = Movie | Book;

# Testing enum
