settings.popupCss:
html {
  background-color: #FAFAFA;
  color: #80CBC4;
  padding: 16px;
}
a {
  color: #8796B0;
  line-height: 16px;
}
.error, .deleted {
  color: #E53935;
}
.success, .inserted {
  color: #91B859;
}
.warning, .modified {
  color: #FFB62C;
}
.type {
  color: #39ADB5;
  font-style: italic;
}
.param {
  color: #F76D47;
}
.current {
  text-decoration: underline;
}
